var CountRecords = Class.create();
CountRecords.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCount: function() {
		var COUNT = "'COUNT', 'sys_mod_count'";
		var table = this.getParameter('sysparm_tablename');
		var agg = new GlideAggregate(table);
		var length = this.getParameter('sysparm_length');
		/*for(var i = 0; i <= length; i++){
			var field = this.getParameter('sysparm_field' + i);
			var value = this.getParameter('sysparm_value' + i);
			agg.addQuery(field, value);
		}*/
		agg.addQuery('priority','=',3);
		agg.setGroup(false);

		agg.addAggregate('COUNT', 'sys_mod_count');
		agg.query();

		if(agg.next()){
			return agg.getAggregate('COUNT', 'sys_mod_count');
		}

		return 5 + '';
	},

    type: 'CountRecords'
});
