sys_script_include.script:
var CountRecords = Class.create();
CountRecords.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getCount: function() {
		var table = this.getParameter('sysparm_tableName');
		var record = new GlideRecord(table);
		var params = this.getParameter('sysparm_queryparams');

		params.forEach(function (obj) {record.addQuery(obj.field, obj.value);});

		record.query();

		return record.getRowCount();

	},

    type: 'CountRecords'
});
